onClick:
{{
ReplanAPI.run(
  {
    body: {
      plan:   GetPlan.data,   // current visible plan
      issues: GetIssues.data  // current open problems
    }
  },
  () => {                     // success: refresh plan + calendar
    GetPlan.run().then(() => {
      // re-run the calendar builder if needed
      // (the HTML/JS snippet above already rerenders on each drag;
      //  here we just ensure the table + calendar stay in sync)
      showAlert('Plan recalculated', 'success');
    });
  },
  (e) => showAlert(e.message, 'error')
)
}}
