customFusionChartConfig:
{
  type: "angulargauge",
  width: "100%",
  height: "220",
  dataFormat: "json",
  dataSource: {
    chart: {
      caption: "On-time delivery",
      lowerLimit: "0",
      upperLimit: "100",
      gaugeFillMix: "{light-10},{light-70},{dark-10}",
      valueBelowPivot: "1",
      showValue: "1",
      theme: "fusion",
      decimals: "1"
    },
    colorRange: {
      color: [
        { minValue: "0",  maxValue: "60",  code: "#d32f2f" },  // red
        { minValue: "60", maxValue: "90",  code: "#ffa000" },  // amber
        { minValue: "90", maxValue: "100", code: "#388e3c" }   // green
      ]
    },
    dials: {
      dial: [
        {
          value: "{{ GetDeliveryPerf.data[0].perf_pct || 0 }}",
          radius: "90%"
        }
      ]
    }
  }
}
