srcDoc.css:
.appsmith-gantt-root {
  height: 100%;
  width: 100%;
  background: #fff;
  border-radius: var(--appsmith-theme-borderRadius);
  box-shadow: var(--appsmith-theme-boxShadow);
  font-family: system-ui;
  padding: 10px 0 0 0;
}

#gantt-container {
  width: 100%;
  height: 450px;
  border-radius: 6px;
  background: #f8f9fb;
  border: 1px solid #eceff1;
}

.gantt .bar {
  stroke: #2066f7;
  fill: #2066f7;
}

.gantt .bar-progress {
  fill: #4caf50;
}

srcDoc.js:
// Frappe Gantt expects data as an array of task objects:
// { id, name, start, end, progress, dependencies }
// We'll use mock data if none available from appsmith.model
defaultData = [{
  id: 'Task1',
  name: 'Design',
  start: '2024-06-13',
  end: '2024-06-16',
  progress: 50
}, {
  id: 'Task2',
  name: 'Development',
  start: '2024-06-17',
  end: '2024-06-20',
  progress: 20,
  dependencies: 'Task1'
}, {
  id: 'Task3',
  name: 'Testing',
  start: '2024-06-21',
  end: '2024-06-22',
  progress: 0,
  dependencies: 'Task2'
}];
function renderGantt(tasks) {
  document.getElementById('gantt-container').innerHTML = '';
  let gantt = new Gantt("#gantt-container", tasks, {
    view_mode: 'Day',
    date_format: 'YYYY-MM-DD',
    custom_popup_html: task => {
      return `<div style="padding:8px;max-width:220px"><b>${task.name}</b><br>${task.start} → ${task.end}<br>Progress: ${task.progress || 0}%</div>`;
    }
  });
}

// Initial load with model data or fallback
renderGantt(appsmith.model.data && appsmith.model.data.length ? appsmith.model.data : defaultData);
appsmith.onModelChange(model => {
  renderGantt(model.data && model.data.length ? model.data : defaultData);
});

