customEChartConfig:
{{ ((chartType) => ( 
({
  type: "hled",             // horizontal LED
  dataFormat: "json",
  width: "100%",
  height: "120",

  dataSource: {
    chart: {
      caption: "Open orders - real-time",
      lowerlimit: "0",
      upperlimit: "50",     // max scale (set to something above worst-case)
      numbersuffix: "",     // no '%' etc.
      showvalue: "1",
      useSameFillColor: "1",
      theme: "fusion"
    },

    colorRange: {
      color: [
        { minValue: "0",    maxValue: "10",  code: "#2e7d32" },   // green  0-10
        { minValue: "10",   maxValue: "20",  code: "#f6ad55" },   // amber 10-20
        { minValue: "20",   maxValue: "50",  code: "#d32f2f" }    // red   >20
      ]
    },

    value: {{ 
      // fetch the single field from your query (falls back to 0 while loading)
      (GetOpenNow.data || [])[0]?.open_now ?? 0 
    }}
  }
})
))(Chart3.chartType) }}

