{{ ((chartType) => ( 
(() => {
  const rows = (getDailyUtil.data || [])
    .filter(r => {
      const d = new Date(r.day);
      return d >= new Date('2019-06-26') &&
             d <= new Date('2019-07-07') &&
             r.resource !== 'grinder';
    });

  const days      = [...new Set(rows.map(r => r.day))]
                    .sort((a,b) => new Date(a)-new Date(b));
  const resources = [...new Set(rows.map(r => r.resource))];

  const data = rows.map(r => [
    days.indexOf(r.day),
    resources.indexOf(r.resource),
    r.util_pct
  ]);

  return {
    tooltip:{
      position:'top',
      formatter:p=>`${resources[p.value[1]]}<br>${days[p.value[0]]}: ${p.value[2]} %`
    },
    grid:{ top:30,left:90,right:15,bottom:55 },        // extra bottom for labels
    xAxis:{
      type:'category',
      position:'bottom',
      offset:2,
      data:days,
      axisLabel:{ rotate:35,margin:6,fontSize:10 },
      axisTick:{ length:3 },
      splitArea:{ show:true }
    },
    yAxis:{
      type:'category',
      data:resources,
      axisLabel:{ fontSize:11 },
      splitArea:{ show:true }
    },
    visualMap:{
      min:0,max:100,orient:'horizontal',left:'center',bottom:4,show:false,
      pieces:[
        { min:0,max:40,color:'#ffffb2' },
        { min:40,max:60,color:'#fed976' },
        { min:60,max:75,color:'#feb24c' },
        { min:75,max:90,color:'#fd8d3c' },
        { min:90,max:100,color:'#e31a1c' }
      ]
    },
    series:[{
      type:'heatmap',
      data,
      itemStyle:{
        borderWidth:0.5,borderColor:'#eeeeee',
        opacity:p=>p.value[2]<5?0.10:1
      },
      emphasis:{ itemStyle:{ shadowBlur:8 } }
    }]
  };
})()
))(Resource_Utilisation_Heatmap.chartType) }}
