customEChartConfig:
{{ ((chartType) => ( 
({
  title: {
    text: 'Average utilisation Feb 19 - Jul 20',
    left: 'center'
  },
  tooltip: {
    trigger: 'item',
    formatter: '{b}: {c}%'
  },
  xAxis: {
    type: 'category',
    data: (GetResourceUtil.data || []).map(r => r.resource),
    axisLabel: { rotate: 30 }
  },
  yAxis: {
    type: 'value',
    max: 100,
    name: '%'
  },
  series: [{
    name: 'Utilisation',
    type: 'bar',
    data: (GetResourceUtil.data || []).map(r => r.util_pct),
    itemStyle: { color: '#ff9999' },    //  ← salmon-pink bars
    label: {
      show: true,
      position: 'top',
      formatter: '{c}%'
    }
  }]
})
))(Resource_Utilization_Chart.chartType) }}

customFusionChartConfig:
{
  "type": "column2d",
  "dataFormat": "json",
  "width": "100%",
  "height": "350",
  "dataSource": {
    "chart": {
      "caption": "Average utilisation Feb-19 → Jul-20",
      "yAxisName": "%",
      "yAxisMaxValue": "100",
      "decimals": "1",
      "theme": "fusion",
      "paletteColors": "#42a5f5",
      "showValues": "1"
    },
    "data": {{ 
      (GetResourceUtil.data || []).map(r => ({
        label: r.resource,
        value: r.util_pct
      }))
    }}
  }
}
